function generateRadarData(data1, data2, data3, data4) {
    var data = [];
    var dataTwo = [];
    var i = 0;
    for (var outCategory in data1) {
        var outCategoryData = data1[outCategory];

        outCategoryData.forEach(function (innerCategory) {
            var rawDataItem = { [innerCategoryType]: innerCategory[0] };

            for (var y = 2; y < innerCategory.length; y++) {
                rawDataItem["value:salary" + (startYear + y - 2)] = innerCategory[y];
            }

            data.push(rawDataItem);
        });

        createRange(outCategory, outCategoryData, i);
        i++;

    }

    for (var outCategory in data2) {
        var outCategoryData = data2[outCategory];

        outCategoryData.forEach(function (innerCategory) {
            var rawDataItem = { [innerCategoryType]: innerCategory[0] };

            for (var y = 2; y < innerCategory.length; y++) {
                rawDataItem["value:tuition" + (startYear + y - 2)] = innerCategory[y];
            }

            data.push(rawDataItem);
        });

        createRange(outCategory, outCategoryData, i);
        i++;

    }

    for (var outCategory in data3) {
        var outCategoryData = data3[outCategory];

        outCategoryData.forEach(function (innerCategory) {
            var rawDataItem = { "industry": innerCategory[0] };

            for (var y = 2; y < innerCategory.length; y++) {
                rawDataItem["value:tuition" + (startYear + y - 2)] = innerCategory[y];
            }

            dataTwo.push(rawDataItem);
        });

        createRange(outCategory, outCategoryData, i);
        i++;

    }

    for (var outCategory in data4) {
        var outCategoryData = data4[outCategory];

        outCategoryData.forEach(function (innerCategory) {
            var rawDataItem = { "industry": innerCategory[0] };

            for (var y = 2; y < innerCategory.length; y++) {
                rawDataItem["value:tuition" + (startYear + y - 2)] = innerCategory[y];
            }

            dataTwo.push(rawDataItem);
        });

        createRange(outCategory, outCategoryData, i);
        i++;

    }
    return [data, dataTwo];
}



function updateRadarData(year) {
    if (currentYear != year) {
        currentYear = year;
        yearLabel.text = String(currentYear);
        series.dataFields.valueY = "value:salary" + currentYear;
        series2.dataFields.valueY = "value:tuition" + currentYear;
        chart.invalidateRawData();
        if (currentID !== undefined) {
            if (flag) {
                var selectData = stateData[currentID];
            } else {
                var selectData = state_data_2[currentID];
            }
            for (var i = 0; i < femaleChart.data.length; i++) {
                femaleChart.data[i].female = selectData[i].female[year - startYear];
                maleChart.data[i].male = selectData[i].male[year - startYear];
            }
            femaleChart.invalidateRawData();
            maleChart.invalidateRawData();
        }
    }
}

function convertString(province) {
    if (province === "Newfoundland and Labrador") {
        return "Newfoundland";
    }
    else if (province === "Prince Edward Island") {
        return "Prince";
    }
    else if (province === "Nova Scotia") {
        return "Scotia";
    }
    else if (province === "New Brunswick") {
        return "Brunswick";
    }
    else if (province === "British Columbia") {
        return "Columbia";
    } else {
        return province;
    }
}

